using AEAssist;
using AEAssist.CombatRoutine;
using AEAssist.CombatRoutine.Module;
using AEAssist.CombatRoutine.Module.Opener;
using AEAssist.Helper;
using AEAssist.JobApi;

namespace ScriptTest;

// 开发工具建议:
// 只是为了学习写脚本，之前没接触过编程 或者没接触过C#的 请使用vs code 并百度[如何在visual studio code里安装.net开发环境]
// 如果有意学习且使用C#语言做更深的开发 建议安装visual studio / rider的最新版 并安装.net环境
// 开发脚本时 请引用AEAssist+Dalamud+其他必要的dll
public class OpenerTest : IOpener
{
    public Action CompeltedAction { get; set; }

    // 技能id 放在前面定义好
    public const uint Flourish = 16013;
    public const uint TechnicalStep = 15998;
    public const uint DoubleStandardFinish = 16192;
    public const uint StandardStep = 15997;
    

    // 如果需要更多步骤 就不停的添加对应的方法即可
    // 如下，留空代表使用ACR提供的起手
    // public List<Action<Slot>> Sequence { get; } = new();
    
    public List<Action<Slot>> Sequence { get; } = new()
    {
        Step0,
        Step1,
    };
    
    
    private static void Step0(Slot slot)
    {
        // Flourish 对自己使用
        slot.Add(new Spell(Flourish, SpellTargetType.Self));
    }
    
    private static void Step1(Slot slot)
    {
        //TechnicalStep 对自己使用
        slot.Add(new Spell(TechnicalStep, SpellTargetType.Self));
    }
    

    // 处理倒计时期间的行为
    public void InitCountDown(CountDownHandler countDownHandler)
    {
        // 倒数14秒的时候跳舞  (如果当前倒计时直接从10秒开始 这个也会触发 多个同时触发的 会按顺序处理)
        countDownHandler.AddAction(14000, BeforeBattle);
        
        // 控制某个ACR的QT 开发工具里要引用对应acr的dll 否则没法让ide检查是否有报错
        AE.Bard.BardRotationEntry.QT.SetQt(AE.Bard.QTKey.UseBaseGcd, false);

        // 期间4个舞步
        countDownHandler.AddAction(12000, GetNextStep);
        countDownHandler.AddAction(10000, GetNextStep);
        countDownHandler.AddAction(8000, GetNextStep);
        countDownHandler.AddAction(6000, GetNextStep);

        // 使用爆发药
        if (AE.Bard.BardRotationEntry.QT.GetQt(AE.Bard.QTKey.UsePotion))
        {
            countDownHandler.AddPotionAction(1500);
        }
        
        // 舞步结束
        countDownHandler.AddAction(100, BattleStart);
    }

    // 跳舞开始
    public static Spell BattleStart()
    {
        return DoubleStandardFinish.GetSpell();
    }

    // 具体的舞步
    public static Spell GetNextStep()
    {
        var Step = Core.Resolve<JobApi_Dancer>().NextStep();
        return Step.GetSpell();
    }
    
    // 舞步结束
    public static Spell BeforeBattle()
    {
        return StandardStep.GetSpell();
    }
}
